const passwordDisplay=document.getElementById("passwordDisplay"),length=document.getElementById("length"),letters=document.getElementById("letters"),numbers=document.getElementById("numbers"),specialChars=document.getElementById("specialCharts"),easyToRem=document.getElementById("easyToRem"),copyBtn=document.getElementById("copyBtn"),regenBtn=document.getElementById("regenBtn");function buildCall(e,n,t,a,r){return"/api/"+e+"/"+n+"/"+t+"/"+a+"/"+r}function generate(){passwordDisplay.innerText="...",fetch(buildCall(length.value,letters.value,numbers.value,specialChars.value,easyToRem.checked)).then(e=>e.json()).then(e=>{"success"!==e.status?alert("An unknown error occurred!"):passwordDisplay.innerText=e.generatedPassword}).catch(()=>{alert("An unknown error occurred!")})}length.onchange=generate,letters.onchange=generate,numbers.onchange=generate,specialChars.onchange=generate,easyToRem.onchange=generate,regenBtn.onclick=generate,copyBtn.onclick=(()=>{navigator.clipboard.writeText(passwordDisplay.innerText)}),generate();